mapper.select:
select class.* ,teacher.name as userName  from `class`
    left join teacher on class.user_id = teacher.id

    class.class_name like concat('%',#{className},'%')
    and class.user_id =#{userId}


         order by id desc

mapper.update:
update `class` set class_name =#{className}
                  create_time=#{time},user_id=#{userId}
where id=#{id}
