mapper.select:
SELECT COUNT(*) AS total FROM user_class uc LEFT JOIN `user` u ON uc.user_id = u.id LEFT JOIN class c ON uc.class_id = c.id
AND uc.class_id IN ( SELECT uc.class_id FROM user_class uc WHERE uc.user_id = #{userId} )
AND uc.class_id = #{classId}

SELECT u.name AS userName, u.username, u.phone, c.class_name AS className, uc.user_id AS userId,
FROM user_class uc LEFT JOIN `user` u ON uc.user_id = u.id LEFT JOIN class c ON uc.class_id = c.id
AND uc.class_id IN ( SELECT uc.class_id FROM user_class uc WHERE uc.user_id = #{userId} )
AND uc.class_id = #{classId}
ORDER BY uc.id DESC LIMIT #{page},#{pageSize}

SELECT uc.user_id AS userId,
FROM user_class uc LEFT JOIN `user` u ON uc.user_id = u.id
AND uc.class_id = #{classId}

SELECT c.class_name AS className, uc.user_id AS teacherId,
FROM user_class uc -- LEFT JOIN `user` u ON uc.user_id = u.id LEFT JOIN `teacher` u ON uc.user_id = u.id LEFT JOIN class c ON c.id = uc.class_id
AND uc.user_id = #{teacherId}
