mapper.select:
select video_upload.* ,teacher.name as userName ,teacher.avatar as userAvatar from `video_upload` left join teacher on video_upload.tid = teacher.id
video_upload.courseName like concat('%',#{courseName},'%') and video_upload.tid =#{tid}
order by id desc

mapper.update:
update `video_upload` set videoPicture =#{videoPicture},content=#{content},
                          time=#{time},tid=#{tid}
where id=#{id}
